module top_module(
			input in,
			input [1:0] state,
			output [1:0] next_state,
			output out
			);

			parameter A=2'd0, B=2'd1, C=2'd2, D=2'd3;

			always@(*)
				begin
					case(state)
						A: next_state <=in?B:A;
						B: next_state <=in?B:C;
						C: next_state <=in?D:A;
						D: next_state <=in?B:C;
					endcase
				end

			assign out = (state == D);
		endmodule

module top_module (
			input clk,
			input reset,   // Synchronous reset
			input x,
			output z
		);

			parameter a = 0, b = 1, c = 2, d = 3, e = 4;
			reg [2:0] state, next_state;

			always @(*) begin
				case (state)
					a: next_state = x ? b : a;
					b: next_state = x ? e : b;
					c: next_state = x ? b : c;
					d: next_state = x ? c : b;
					e: next_state = x ? e : d;
				endcase
			end

			always @(posedge clk) begin
				if (reset) begin
					state <= a;
				end
				else begin
					state <= next_state;
				end
			end

			assign z = (state == d || state == e);

		endmodule

module top_module (
			input clk,
			input resetn,    // active-low synchronous reset
			input x,
			input y,
			output reg f,
			output reg g
		);

			parameter A=4'd0, f1=4'd1, tmp0=4'd2, tmp1=4'd3, tmp2=4'd4, g1=4'd5, g1p=4'd6, tmp3=4'd7, g0p=4'd8;
			reg [3:0] state, next_state;

			always@(*) begin
				case(state)
					A: begin
						if(resetn)
							next_state = f1;
						else
							next_state = A;
					end
					f1:     next_state = tmp0;
					tmp0: begin
						if(x)
							next_state = tmp1;
						else
							next_state = tmp0;
					end
					tmp1: begin
						if(~x)
							next_state = tmp2;
						else
							next_state = tmp1;
					end
					tmp2: begin
						if(x)
							next_state = g1;
						else
							next_state = tmp0;
					end
					g1: begin
						if(y)
							next_state = g1p;
						else
							next_state = tmp3;
					end
					tmp3: begin
						if(y)
							next_state = g1p;
						else
							next_state = g0p;
					end
					g1p: begin
						if(~resetn)
							next_state = A;
						else
							next_state = g1p;
					end
					g0p: begin
						if(~resetn)
							next_state = A;
						else
							next_state = g0p;
					end
				endcase
			end

			always@(posedge clk) begin
				if(~resetn)
					state <= A;
				else
					state <= next_state;
			end

			always@(posedge clk) begin
				case(next_state)
					f1:     f <= 1'b1;
					g1:		g <= 1'b1;
					tmp3:	g <= 1'b1;
					g1p:    g <= 1'b1;
					g0p:    g <= 1'b0;
					default: begin
							f <= 1'b0;
							g <= 1'b0;
					end
				endcase
			end

		endmodule

