module top_module (
			input clk,
			input reset,      // Synchronous reset
			input data,
			output start_shifting
		);

			localparam [2:0] IDLE = 0,
							S1   = 1,
							S11  = 2,
							S110 = 3,
							S1101= 4;

			reg [2:0] state, next;

			always @(*) begin
				case (state)
					IDLE  : next = (data) ? S1   : IDLE;
					S1    : next = (data) ? S11  : IDLE;
					S11   : next = (data) ? S11  : S110;
					S110  : next = (data) ? S1101: IDLE;
					S1101 : next = S1101;
				endcase
			end

			always @(posedge clk) begin
				if (reset) begin
					state <= IDLE;
				end
				else state <= next;
			end

			assign start_shifting = (state == S1101);
		endmodule

module top_module (
			input clk,
			input aresetn,    // Asynchronous active-low reset
			input x,
			output z
		);

			localparam [1:0] IDLE     = 0,
							ONE      = 1,
							ONE_ZERO = 2;

			reg [1:0] state, next;

			always @(*) begin
				case (state)
					IDLE : begin
						next = (x) ? ONE : IDLE;
						z = 0;
					end
					ONE : begin
						next = (x) ? ONE : ONE_ZERO;
						z = 0;
					end
					ONE_ZERO : begin
						if (x) begin
							next = ONE;
							z = 1;
						end
						else begin
							next = IDLE;
							z = 0;
						end
					end
				endcase
			end

			always @(posedge clk or negedge aresetn) begin
				if (~aresetn) state <= IDLE;
				else state <= next;
			end

		endmodule

