module top_module (
			input clk,
			input reset,   // Synchronous reset
			input s,
			input w,
			output z
		);
		parameter A = 0, B = 1;

		reg state, next;
			reg [1:0] count;
			reg [1:0] count1;

			always @(*) begin
				case(state)
					A : next = (s) ? B : A;
					B : next = B;
		endcase
		end

		always @(posedge clk) begin
		if (reset) begin
		state <= A;
					count=0;
					count1=0;
		end
		else begin
			state <= next;

				if(state==B)
					begin
						if(count1==3)begin
							count=0;
							count1=0;
						end
						if(w==1) count=count+1;

							count1=count1+1;

					end
			end
		end



			assign z = ((count == 2) & (count1 == 3) );
		endmodule

module top_module(
			input clk,
			input [7:0] in,
			input reset,    // Synchronous reset
			output done
		);

			localparam [1:0] BYTE1 = 2'b00,
							BYTE2 = 2'b01,
							BYTE3 = 2'b10,
							DONE  = 2'b11;

			reg [1:0] state, next;

			// State transition logic (combinational)
			always @(*) begin
				case(state)
					BYTE1 : next = (in[3]) ? BYTE2 : BYTE1;
					BYTE2 : next = BYTE3;
					BYTE3 : next = DONE;
					DONE  : next = (in[3]) ? BYTE2 : BYTE1;
				endcase
			end

			// State flip-flops (sequential)
			always @(posedge clk) begin
				if(reset) state <= BYTE1;
				else state <= next;
			end

			// Output logic
			assign done = (state == DONE);

		endmodule

module top_module(
			input clk,
			input areset,    // Freshly brainwashed Lemmings walk left.
			input bump_left,
			input bump_right,
			output walk_left,
			output walk_right
		); //

			parameter LEFT = 0, RIGHT = 1;
			reg state, next_state;

			always @(*) begin
				// State transition logic
				case(state)
					LEFT : next_state = (bump_left) ? RIGHT : LEFT;
					RIGHT : next_state = (bump_right) ? LEFT : RIGHT;
				endcase
			end

			always @(posedge clk, posedge areset) begin
				// State flip-flops with asynchronous reset
				if(areset) state <= LEFT;
				else begin
					state <= next_state;
				end
			end

			// Output logic
			assign walk_left = (state == LEFT);
			assign walk_right = (state == RIGHT);

		endmodule

module top_module(
			input clk,
			input areset,    // Freshly brainwashed Lemmings walk left.
			input bump_left,
			input bump_right,
			input ground,
			output walk_left,
			output walk_right,
			output aaah
		);

			localparam [1:0] WALK_L = 2'b00,
							WALK_R = 2'b01,
							FALL_L = 2'b10,
							FALL_R = 2'b11;

			reg [1:0] state, next;

			always @(posedge clk or posedge areset) begin
				if(areset) state <= WALK_L;
				else begin
					state <= next;
				end
			end

			always @(*) begin
				case(state)
					WALK_L : next = (ground == 0) ? FALL_L : ((bump_left == 1) ? WALK_R : WALK_L);
					WALK_R : next = (ground == 0) ? FALL_R : ((bump_right == 1) ? WALK_L : WALK_R);
					FALL_L : next = (ground == 1) ? WALK_L : FALL_L;
					FALL_R : next = (ground == 1) ? WALK_R : FALL_R;
				endcase
			end

			assign walk_left = (state == WALK_L);
			assign walk_right = (state == WALK_R);
			assign aaah = ((state == FALL_L) || (state == FALL_R));

		endmodule

module top_module(
			input clk,
			input areset,    // Freshly brainwashed Lemmings walk left.
			input bump_left,
			input bump_right,
			input ground,
			input dig,
			output walk_left,
			output walk_right,
			output aaah,
			output digging
		);

			localparam [2:0] WALK_L = 3'b000,
							WALK_R = 3'b001,
							FALL_L = 3'b010,
							FALL_R = 3'b011,
							DIG_L  = 3'b100,
							DIG_R  = 3'b101;

			reg [2:0] state, next;

			always @(posedge clk or posedge areset) begin
				if(areset) state <= WALK_L;
				else state <= next;
			end

			always @(*) begin
				case(state)
					WALK_L : begin
						if(!ground) next = FALL_L;
						else begin
							if(dig) next = DIG_L;
							else begin
								if(bump_left) next = WALK_R;
								else next = WALK_L;
							end
						end
					end
					WALK_R : begin
						if(!ground) next = FALL_R;
						else begin
							if(dig) next = DIG_R;
							else begin
								if(bump_right) next = WALK_L;
								else next = WALK_R;
							end
						end
					end
					FALL_L : next = (ground) ? WALK_L : FALL_L;
					FALL_R : next = (ground) ? WALK_R : FALL_R;
					DIG_L  : next = (ground) ? DIG_L : FALL_L;
					DIG_R  : next = (ground) ? DIG_R : FALL_R;
				endcase
			end

			assign walk_left = (state == WALK_L);
			assign walk_right = (state == WALK_R);
			assign aaah = ((state == FALL_L) || (state == FALL_R));
			assign digging = ((state == DIG_L) || (state == DIG_R));

		endmodule

module top_module(
			input clk,
			input areset,    // Freshly brainwashed Lemmings walk left.
			input bump_left,
			input bump_right,
			input ground,
			input dig,
			output walk_left,
			output walk_right,
			output aaah,
			output digging
		);

			localparam [2:0] WALK_L = 3'b000,
							WALK_R = 3'b001,
							FALL_L = 3'b010,
							FALL_R = 3'b011,
							DIG_L  = 3'b100,
							DIG_R  = 3'b101,
							SPLATTER = 3'b110;

			reg [2:0] state, next;
			reg [6:0] count;

			always @(posedge clk or posedge areset) begin
				if(areset) state <= WALK_L;
				else if(state == FALL_R || state == FALL_L) begin
					count <= count + 1;
					state <= next;
				end
				else begin
					state <= next;
					count <= 0;
				end
			end

			always @(*) begin
				case(state)
					WALK_L : begin
						if(!ground) next = FALL_L;
						else begin
							if(dig) next = DIG_L;
							else begin
								if(bump_left) next = WALK_R;
								else next = WALK_L;
							end
						end
					end
					WALK_R : begin
						if(!ground) next = FALL_R;
						else begin
							if(dig) next = DIG_R;
							else begin
								if(bump_right) next = WALK_L;
								else next = WALK_R;
							end
						end
					end
					FALL_L : begin
						if(ground) begin
							if(count > 19) next = SPLATTER;
							else next = WALK_L;
						end
						else next = FALL_L;
					end
					FALL_R : begin
						if(ground) begin
							if(count > 19) next = SPLATTER;
							else next = WALK_R;
						end
						else next = FALL_R;
					end
					DIG_L  : next = (ground) ? DIG_L : FALL_L;
					DIG_R  : next = (ground) ? DIG_R : FALL_R;
					SPLATTER : next = SPLATTER;
				endcase
			end

			assign walk_left = (state == WALK_L);
			assign walk_right = (state == WALK_R);
			assign aaah = ((state == FALL_L) || (state == FALL_R));
			assign digging = ((state == DIG_L) || (state == DIG_R));

		endmodule

