module_declaration:
module top_module( 
	input [31:0] in,
	output [31:0] out 
);

output:
module top_module( 
	input [31:0] in,
	output [31:0] out 
);

	assign out[31:24] = in[7:0];
	assign out[23:16] = in[15:8];
	assign out[15:8]  = in[23:16];
	assign out[7:0]   = in[31:24];

endmodule

module top_module( 
	input [2:0] a,
	input [2:0] b,
	output [2:0] out_or_bitwise,
	output out_or_logical,
	output [5:0] out_not
);

	assign out_or_bitwise = a | b;
	assign out_or_logical = a || b;
	assign out_not[5:3] = ~(b[2:0]);
	assign out_not[2:0] = ~(a[2:0]);

endmodule

module_declaration:
module top_module( 
	input [3:0] in,
	output out_and,
	output out_or,
	output out_xor
);

output:
module top_module( 
	input [3:0] in,
	output out_and,
	output out_or,
	output out_xor
);

	assign out_and = (in[3] & in[2] & in[1] & in[0]);
	assign out_or  = (in[3] | in[2] | in[1] | in[0]);
	assign out_xor = (in[3] ^ in[2] ^ in[1] ^ in[0]);

endmodule

