endmodule

module_declaration:
module top_module(
	input [31:0] a,
	input [31:0] b,
	output [31:0] sum
);

output:
module top_module(
	input [31:0] a,
	input [31:0] b,
	output [31:0] sum
);
	wire w,x;
	add16 a1(a[15:0],b[15:0],1'b0,sum[15:0],w);
	add16 a2(a[31:16],b[31:16],w,sum[31:16],x);
endmodule

module_declaration:
module top_module(
	input [31:0] a,
	input [31:0] b,
	output [31:0] sum
);

output:
module top_module(
	input [31:0] a,
	input [31:0] b,
	output [31:0] sum
);

	wire w1;
	reg x = 0;

	add16 inst0(.a(a[15:0]), .b(b[15:0]),  .sum(sum[15:0]),  .cin(x), .cout(w1));
	add16 inst1(.a(a[31:16]), .b(b[31:16]), .sum(sum[31:16]), .cin(w1));
endmodule

module top_module (
	input too_cold,
	input too_hot,
	input mode,
	input fan_on,
	output heater,
	output aircon,
	output fan
);

	assign heater = mode & too_cold;
	assign aircon = (~mode) & too_hot;
	assign fan = fan_on | (heater | aircon);

endmodule

module_declaration:
module top_module( 
	input [2:0] in,
	output [1:0] out 
);

output:
module top_module( 
	input [2:0] in,
	output [1:0] out 
);

	integer i;

	always @(*) begin
		out = 2'b0;
		for(i = 0; i < 3; i ++) begin
			if(in[i])
				out = out + 2'b1;
		end
	end

endmodule

module_declaration:
module top_module( 
	input a, b, sel,
	output out 
);

output:
module top_module( 
	input a, b, sel,
	output out 
);

	assign out = sel ? b : a;

endmodule

module_declaration:
module top_module( 
	input [15:0] a, b, c, d, e, f, g, h, i,
	input [3:0] sel,
	output [15:0] out 
);

output:
module top_module( 
	input [15:0] a, b, c, d, e, f, g, h, i,
	input [3:0] sel,
	output [15:0] out 
);

	always@(*)begin
		case(sel)
			4'h0 : out = a;
			4'h1 : out = b;
			4'h2 : out = c;
			4'h3 : out = d;
			4'h4 : out = e;
			4'h5 : out = f;
			4'h6 : out = g;
			4'h7 : out = h;
			4'h8 : out = i;
			default : out = 16'hFFFF;
		endcase
	end

endmodule

