	assign cout = a & b | a & cin | b & cin;

endmodule

module_declaration:
module top_module (
	input [7:0] a,
	input [7:0] b,
	output [7:0] s,
	output overflow
);

output:
module top_module (
	input [7:0] a,
	input [7:0] b,
	output [7:0] s,
	output overflow
);

	assign s = a + b;
	assign overflow = (~(a[7] ^ b[7])) & (s[7] != a[7]);

endmodule

module_declaration:
module top_module( 
	input [2:0] a, b,
	input cin,
	output [2:0] cout,
	output [2:0] sum 
);

output:
module top_module( 
	input [2:0] a, b,
	input cin,
	output [2:0] cout,
	output [2:0] sum 
);
	wire [2:0] cout1;
	fadd f1(a[0],b[0],cin,cout1[0],sum[0]);
	fadd f2(a[1],b[1],cout1[0],cout1[1],sum[1]);
	fadd f3(a[2],b[2],cout1[1],cout1[2],sum[2]);
	assign cout = cout1;
endmodule

module fadd( 
	input a, b, cin,
	output cout, sum 
);
	assign sum = a^b^cin;
	assign cout = (a&b)|(b&cin)|(a&cin);
endmodule

module top_module (
	input a,
	input b,
	input c,
	input d,
	output out_sop,
	output out_pos
); 

	assign out_sop = (c & d) | (~a & ~b & c);
	assign out_pos = (c) & (~a | b) & (d | ~b);

endmodule

module_declaration:
module top_module (
	input clk,
	input areset,   // active high asynchronous reset
	input [7:0] d,
	output [7:0] q
);

output:
module top_module (
	input clk,
	input areset,
	input [7:0] d,
	output [7:0] q
);

	always@(posedge clk,posedge areset)begin
		if(areset)
			q <= 0;
		else
			q <= d;
	end

endmodule

