module top_module(
	input clk,
	input reset,
	input ena,
	output pm,
	output [7:0] hh,
	output [7:0] mm,
	output [7:0] ss
); 
	always@(posedge clk)
		begin
			if(reset) begin hh[3:0]<=4'd2;hh[7:4]<=4'd1; mm<=8'h00; ss<=8'h00; pm<=1'b0; end
			else if(ena)
				begin
					if(ss[3:0] != 4'd9) ss[3:0]<=ss[3:0]+4'd1;
					else
						begin
							ss[3:0]<=4'd0;
							ss[7:4]<=ss[7:4]+4'd1;
							if(ss[7:4]== 4'd5) 
								begin
									ss[7:4]<=4'd0;
									mm[3:0]<=mm[3:0]+4'd1;
									if(mm[3:0] == 4'd9)
										begin
											mm[3:0]<=4'd0;
											mm[7:4]<=mm[7:4]+4'd1;
											if(mm[7:4] == 4'd5)
												begin
													mm[7:4]<=4'd0;
													hh[3:0]<=hh[3:0]+4'd1;
													if(hh[3:0] == 4'd9)
														begin
															hh[3:0]<=4'd0;
															hh[7:4]<=hh[7:4]+4'd1;
														end
													if(hh[3:0] == 4'd1 && hh[7:4]==4'd1)
														begin
															pm<=~pm;
															hh[3:0]<=4'd2;
															hh[7:4]<=4'd1;
														end
													if(hh[3:0] == 4'd2 && hh[7:4]==4'd1)
														begin
															hh[3:0]<=4'd1;
															hh[7:4]<=4'd0;
														end
												end
										end
								end
						end
				end
		end
endmodule

module top_module(
	input clk,
	input reset,
	input ena,
	output pm,
	output [7:0] hh,
	output [7:0] mm,
	output [7:0] ss
); 

	always@(posedge clk) begin
		if (reset) begin
			hh <= 8'h12;
			mm <= 0;
			ss <= 0;
			pm <= 0;
		end
		else begin
			if (ena) begin
				pm <= (hh == 8'h11 && mm == 8'h59 && ss == 8'h59)? ~pm : pm;
				if (ss == 8'h59) begin // ss = 59
					ss <= 0;

					if (mm == 8'h59) begin // mm = 59
						mm <= 0;

						if (hh == 8'h12) begin
							hh <= 8'h01;
						end

						else begin
							if (hh[3:0] == 4'd9) begin
								hh[3:0] <= 0;
								hh[7:4] <= hh[7:4] + 4'd1;
							end
							else 
								hh[3:0] <= hh[3:0] + 4'd1;  
						end

					end
					else begin
						if (mm[3:0] == 4'd9) begin
							mm[3:0] <= 0;
							mm[7:4] <= mm[7:4] + 4'd1;
						end
						else 
							mm[3:0] <= mm[3:0] + 4'd1; 
					end


				end
				else begin
					if (ss[3:0] == 4'd9) begin
						ss[3:0] <= 0;
						ss[7:4] <= ss[7:4] + 4'd1;
					end
					else 
						ss[3:0] <= ss[3:0] + 4'd1;
				end
			end   
		end
	end

endmodule

module top_module (
	input clk,
	input enable,
	input S,
	input A, B, C,
	output Z
); 

	reg[7:0] q;

	always @(posedge clk) begin
		if(enable)
			q <= {q[6:0], S};	
		else 
	q <= q;
	end

	always @(*) begin
	case({A, B, C})
		3'b000 : Z = q[0];
		3'b001 : Z = q[1];
		3'b010 : Z = q[2];
		3'b011 : Z = q[3];
		3'b100 : Z = q[4];
		3'b101 : Z = q[5];
		3'b110 : Z = q[6];
		3'b111 : Z = q[7];
	endcase
	end

endmodule

module_declaration:
module top_module(
	input clk,
	input load,
	input [255:0] data,
	output [255:0] q 
);

module top_module(
	input clk,
	input load,
	input [255:0] data,
	output [255:0] q 
);

output:
module top_module(
	input clk,
	input load,
	input [255:0] data,
	output [255:0] q 
); 

	reg [255:0] temp;
	wire [287:0] map = {temp[15:0],temp,temp[255:240]};
	int count;

	always@(posedge clk) begin
		if(load) begin
			q <= data;
		end
		else begin
			for (int i = 16; i < 272; i = i+1) begin
				if(i == 16 || i == 32 || i == 48 || i == 64 || i == 80 || i == 96 || i==112 || i==128 || i == 144
				|| i == 160 || i == 176 || i == 192 || i == 208 || i == 224 || i == 240 || i == 256) begin
					count = map[i+1]+map[i+15]+map[i+16]+map[i+17]+map[i+31]+map[i-1]+map[i-15]+map[i-16];
				end
				else if (i == 31 || i == 47 || i == 63 || i == 79 || i == 95 || i==111 || i==127 || i == 143
				|| i == 159 || i == 175 || i == 191 || i == 207 || i == 223 || i == 239 || i == 255 || i == 271) begin
					count = map[i-1]+map[i-15]+map[i-16]+map[i-17]+map[i-31]+map[i+1]+map[i+15]+map[i+16];
				end
				else begin
					count = map[i+1]+map[i+15]+map[i+16]+map[i+17]+map[i-1]+map[i-15]+map[i-16]+map[i-17];
				end

				if(count == 2)
					q[i-16] <= q[i-16];
				else if(count == 3)
					q[i-16] <= 1'b1;
				else
					q[i-16] <= 1'b0;
			end
		end

	end

	always@(negedge clk) begin
		temp <= q;
	end
endmodule

module_declaration:
module top_module(
	input in,
	input [1:0] state,
	output [1:0] next_state,
	output out
);

output:
module top_module(
	input in,
	input [1:0] state,
	output [1:0] next_state,
	output out
	); 

	parameter A=2'd0, B=2'd1, C=2'd2, D=2'd3;

	always@(*)
		begin
			case(state)
				A: next_state <=in?B:A;
				B: next_state <=in?B:C;
				C: next_state <=in?D:A;
				D: next_state <=in?B:C;
			endcase
		end

	assign out = (state == D);
endmodule

module_declaration:
module top_module (
	input clk,
	input reset,   // Synchronous reset
	input x,
	output z
);

output:
module top_module (
	input clk,
	input reset,   // Synchronous reset
	input x,
	output z
);

	parameter a = 0, b = 1, c = 2, d = 3, e = 4;
	reg [2:0] state, next_state;

	always @(*) begin
		case (state)
			a: next_state = x ? b : a;
			b: next_state = x ? e : b;
			c: next_state = x ? b : c;
			d: next_state = x ? c : b;
			e: next_state = x ? e : d;
		endcase
	end

	always @(posedge clk) begin
		if (reset) begin
			state <= a;
		end
		else begin
			state <= next_state;
		end
	end

	assign z = (state == d || state == e);

endmodule

module top_module (
	input clk,
	input resetn,    // active-low synchronous reset
	input x,
	input y,
	output reg f,
	output reg g
); 

	parameter A=4'd0, f1=4'd1, tmp0=4'd2, tmp1=4'd3, tmp2=4'd4, g1=4'd5, g1p=4'd6, tmp3=4'd7, g0p=4'd8;
	reg [3:0] state, next_state;

	always@(*) begin
		case(state)
			A: begin
				if(resetn) 
					next_state = f1;
				else
					next_state = A;
			end
			f1:     next_state = tmp0;
			tmp0: begin
				if(x)
					next_state = tmp1;
				else
					next_state = tmp0;
			end
			tmp1: begin
				if(~x)
					next_state = tmp2;
				else
					next_state = tmp1;
			end
			tmp2: begin
				if(x)
					next_state = g1;
				else
					next_state = tmp0;
			end
			g1: begin
				if(y)
					next_state = g1p;
				else
					next_state = tmp3;
			end
			tmp3: begin
				if(y)
					next_state = g1p;
				else
					next_state = g0p;
			end
			g1p: begin
				if(~resetn)
					next_state = A;
				else
					next_state = g1p;
			end
			g0p: begin
				if(~resetn)
					next_state = A;
				else
					next_state = g0p;
			end
		endcase
	end

	always@(posedge clk) begin
		if(~resetn)
			state <= A;
		else
			state <= next_state;
	end

	always@(posedge clk) begin
		case(next_state)
			f1:     f <= 1'b1;
			g1:		g <= 1'b1;
			tmp3:	g <= 1'b1;
			g1p:    g <= 1'b1;
			g0p:    g <= 1'b0;
			default: begin
					f <= 1'b0;
					g <= 1'b0;
			end
		endcase
	end

endmodule

module_declaration:
module top_module (
	input clk,
	input reset,
	output [9:0] q
);

output:
module top_module (
	input clk,
	input reset,
	output [9:0] q
);

	always @(posedge clk) begin
		if (reset) q <= 0;
		else begin
		if (q < 999) q <= q + 1'b1;
			else q <= 0;
		end
	end

endmodule

module top_module (
	input clk,
	input shift_ena,
	input count_ena,
	input data,
	output [3:0] q
);

	reg [3:0] shift_temp;

	always @(posedge clk) begin
		if (shift_ena) begin
			shift_temp <= {shift_temp[2:0], data};
		end
		else if (count_ena) begin
			shift_temp <= shift_temp - 1'b1;
		end 
	end

	assign q = shift_temp;

endmodule
